CREATE OR REPLACE PROCEDURE ptu_obter_se_consiste_prest ( cd_inconsistencia_p bigint, nr_seq_tipo_prestador_p bigint, ds_inscricao_estadual_p text, ie_consiste_p INOUT text) AS $body$
DECLARE


/*Finalidade: Verificar se deve realizar a consistencia no A400/450
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
*/
ie_consiste_w		varchar(10) := 'S';
ds_inscricao_estadual_w	varchar(255);

C01 CURSOR FOR
	SELECT	a.ie_consiste
	from	ptu_inconsistencia_prest b,
		ptu_incons_prest_regra a
	where	b.nr_sequencia			= a.nr_seq_inconsistencia
	and	b.cd_inconsistencia		= cd_inconsistencia_p
	and (a.nr_seq_tipo_prestador	= nr_seq_tipo_prestador_p or coalesce(a.nr_seq_tipo_prestador::text, '') = '')
	and (upper(a.nr_inscricao_estadual)	= ds_inscricao_estadual_w or coalesce(a.nr_inscricao_estadual::text, '') = '')
	order by a.nr_seq_tipo_prestador;


BEGIN
ds_inscricao_estadual_w	:= upper(trim(both ds_inscricao_estadual_p));

open C01;
loop
fetch C01 into
	ie_consiste_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

ie_consiste_p := coalesce(ie_consiste_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
