CREATE OR REPLACE PROCEDURE ptu_processa_decurso_prazo ( nr_seq_dec_prazo_p ptu_decurso_prazo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_confirmacao_p INOUT ptu_confirmacao.nr_sequencia%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Realizar a validação do arquivo de 00700 - Decurso de Prazo do PTU
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ x] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção: Performace
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
nr_seq_execucao_w	ptu_decurso_prazo.nr_seq_guia%type;
cd_unimed_executora_w	ptu_confirmacao.cd_unimed_executora%type;


BEGIN

cd_estabelecimento_w := cd_estabelecimento_p;

--Quando as transações são geradas pelo WebService, não existe estabelecimento definido, então é verificado o estabeleicmento do parâmetro
if ( coalesce(cd_estabelecimento_w::text, '') = ''	) then
	cd_estabelecimento_w := ptu_obter_estab_padrao;
end if;
CALL ptu_gestao_envio_confirmacao(nr_seq_dec_prazo_p, cd_estabelecimento_w, 'DP', nm_usuario_p);

select	cd_unimed_executora,
	nr_seq_execucao
into STRICT	cd_unimed_executora_w,
	nr_seq_execucao_w
from	ptu_decurso_prazo
where	nr_sequencia = nr_seq_dec_prazo_p;

begin
	select	nr_sequencia
	into STRICT	nr_seq_confirmacao_p
	from	ptu_confirmacao
	where	nr_seq_execucao = nr_seq_execucao_w
	and	cd_unimed_executora = cd_unimed_executora_w
	and	ie_tipo_resposta = 'DP';
exception
when others then
	nr_seq_confirmacao_p := null;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
