CREATE OR REPLACE PROCEDURE ptu_processa_pedido_cancel_v60 ( nr_seq_pedido_cancel_p ptu_cancelamento.nr_sequencia%type, ie_possui_pedido_p text, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, nr_seq_confirmacao_p INOUT ptu_confirmacao.nr_sequencia%type) AS $body$
DECLARE

 
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type;
ie_tipo_resposta_w	varchar(5);
nr_seq_origem_w		ptu_cancelamento.nr_seq_origem%type;	
 

BEGIN 
 
cd_estabelecimento_w := cd_estabelecimento_p;
 
--Quando as transações são geradas pelo WebService, não existe estabelecimento definido, então é verificado o estabeleicmento do parâmetro 
if ( coalesce(cd_estabelecimento_w::text, '') = ''	) then 
	cd_estabelecimento_w := ptu_obter_estab_padrao;
end if;
 
--Se já existir um pedido de cancelamento o sistema não gera nenhuma registro na base 
if ( ie_possui_pedido_p = 'N' ) then 
	--Rotina utilizada para gerar atualizar algumas informações no pedido de cancelamento 
	--ptu_atualizar_pedido_cancel( nr_seq_pedido_cancel_p, nm_usuario_p); 
 
	--Rotina utilizada para gerar o cancelamento da guia ou requisição 
	SELECT * FROM ptu_gerar_cancel_guia_req( nr_seq_pedido_cancel_p, cd_estabelecimento_p, nm_usuario_p, ie_tipo_resposta_w, nr_seq_origem_w) INTO STRICT ie_tipo_resposta_w, nr_seq_origem_w;
 
	---Rotina utilizada para 
	CALL ptu_gestao_envio_confirmacao(nr_seq_origem_w, cd_estabelecimento_p, ie_tipo_resposta_w, nm_usuario_p);
end if;
 
--Retorna a sequência da resposta de confirmação gerada para o 00311 - Pedido de cancelamento, esta sequência é utilizada pelo WebService para gerar o XML de resposta do pedido 
select (select	max(nr_sequencia) 
	from	ptu_confirmacao c 
	where	c.nr_seq_execucao	= a.nr_seq_execucao 
	and	c.cd_unimed_executora	= a.cd_unimed_executora) nr_seq_resp_ped_aut 
into STRICT	nr_seq_confirmacao_p 
from	ptu_cancelamento a 
where	a.nr_sequencia = nr_seq_pedido_cancel_p;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
