CREATE OR REPLACE PROCEDURE ptu_reenviar_fatura ( ds_motivo_p text, dt_reenvio_p timestamp, nr_seq_fatura_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_devolucao_w	bigint;
nr_titulo_w		bigint;


BEGIN
if (ds_motivo_p IS NOT NULL AND ds_motivo_p::text <> '') and (dt_reenvio_p IS NOT NULL AND dt_reenvio_p::text <> '') then
	select	max(nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber
	where	nr_seq_ptu_fatura = nr_seq_fatura_p;

	select	max(nr_sequencia)
	into STRICT	nr_seq_devolucao_w
	from	titulo_receber_devolucao
	where	nr_titulo = nr_titulo_w;

	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then
		update	titulo_receber_devolucao
		set	ds_motivo	= ds_motivo_p,
			dt_reenvio	= dt_reenvio_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao 	= clock_timestamp()
		where	nr_sequencia 	= nr_seq_devolucao_w;

		update	titulo_receber
		set	dt_ultima_devolucao  = NULL,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao 	= clock_timestamp()
		where	nr_titulo 	    = nr_titulo_w;

		update	ptu_fatura
		set	nm_usuario		= nm_usuario_p,
			dt_atualizacao		= clock_timestamp(),
			nr_seq_mot_devolucao	 = NULL,
			nm_usuario_devolucao	 = NULL,
			dt_devolucao		 = NULL
		where	nr_sequencia		= nr_seq_fatura_p;

		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
