CREATE OR REPLACE PROCEDURE ptu_reenvia_compl_autorizacao ( nr_seq_compl_w bigint, nm_usuario_p text) AS $body$
DECLARE


ie_utilizado_w			varchar(2);


BEGIN
select	ie_utilizado
into STRICT	ie_utilizado_w
from	ptu_pedido_compl_aut
where	nr_sequencia	= nr_seq_compl_w;

if (ie_utilizado_w	= 'S') then
	update	ptu_pedido_compl_aut
	set	ie_utilizado	= 'N',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_compl_w;

	update	ptu_pedido_compl_aut_serv
	set	ie_utilizado	= 'N',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_seq_pedido	= nr_seq_compl_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
