CREATE OR REPLACE PROCEDURE ptu_resposta_pedido_auditoria ( nr_seq_requisicao_p text, nr_seq_complemento_p text, ie_tipo_processo_p INOUT text, ie_estagio_p INOUT text, ie_glosa_acao_ptu_p INOUT text, ie_ocorr_acao_ptu_p INOUT text, qt_requisicao_proc_p INOUT bigint, qt_requisicao_mat_p INOUT bigint, nm_usuario_p text) AS $body$
DECLARE


ie_tipo_processo_w		varchar(1);
ie_estagio_w		smallint;
ie_glosa_acao_ptu_w	varchar(2);
ie_ocorr_acao_ptu_w	varchar(2);
qt_requisicao_proc_w	bigint;
qt_requisicao_mat_w	bigint;


BEGIN

select	max(ie_tipo_processo),
	max(ie_estagio)
into STRICT	ie_tipo_processo_w,
	ie_estagio_w
from	pls_requisicao
where	nr_sequencia = nr_seq_requisicao_p;

select	substr(pls_obter_se_glosa_acao_ptu(null, nr_seq_requisicao_p, nr_seq_complemento_p),1,4)
into STRICT	ie_glosa_acao_ptu_w
;

select	substr(pls_obter_se_ocorr_acao_ptu(null, nr_seq_requisicao_p, nr_seq_complemento_p),1,4)
into STRICT	ie_ocorr_acao_ptu_w
;

select	count(*)
into STRICT	qt_requisicao_proc_w
from	pls_requisicao_proc
where	nr_seq_requisicao = nr_seq_requisicao_p
and	ie_status in ('N','G');

select	count(*)
into STRICT	qt_requisicao_mat_w
from	pls_requisicao_mat
where	nr_seq_requisicao = nr_seq_requisicao_p
and	ie_status in ('N','G');

ie_tipo_processo_p := ie_tipo_processo_w;
ie_estagio_p := ie_estagio_w;
ie_glosa_acao_ptu_p := ie_glosa_acao_ptu_w;
ie_ocorr_acao_ptu_p := ie_ocorr_acao_ptu_w;
qt_requisicao_proc_p := qt_requisicao_proc_w;
qt_requisicao_mat_p := qt_requisicao_mat_w;

commit;

end;
$body$
