CREATE OR REPLACE PROCEDURE ptu_substituir_item_imp_a700 ( nr_seq_nota_serv_p ptu_nota_servico.nr_sequencia%type, cd_item_alterado_p bigint, ie_origem_proc_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

			
/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Substituir no item do material ou procedimento importado do A700
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
			
ie_tipo_tabela_w	ptu_nota_servico.ie_tipo_tabela%type;
nr_seq_nota_cobr_w	ptu_nota_servico.nr_seq_nota_cobr%type;


BEGIN
select	ie_tipo_tabela,
	nr_seq_nota_cobr
into STRICT	ie_tipo_tabela_w,
	nr_seq_nota_cobr_w
from	ptu_nota_servico
where	nr_sequencia	= nr_seq_nota_serv_p;

if (ie_tipo_tabela_w in (0,1,4)) then
	update	ptu_nota_servico
	set	cd_procedimento		= cd_item_alterado_p,
		ie_origem_proced	= ie_origem_proc_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_nota_serv_p;
	
elsif (ie_tipo_tabela_w in (2,3,5,6)) then
	update	ptu_nota_servico
	set	nr_seq_material		= cd_item_alterado_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_nota_serv_p;
end if;

CALL ptu_gerar_inco_receb_a700_pck.ptu_gerar_inconsistencias(null,nr_seq_nota_cobr_w,nm_usuario_p,cd_estabelecimento_p);

commit;
end;
$body$
