CREATE OR REPLACE PROCEDURE ptu_vincular_benef_nota_cobr ( nr_seq_nota_cobr_p ptu_nota_cobranca.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


cd_usuario_plano_w	pls_segurado_carteira.cd_usuario_plano%type;


BEGIN

select	max(cd_usuario_plano)
into STRICT	cd_usuario_plano_w
from	pls_segurado_carteira
where	nr_seq_segurado	= nr_seq_segurado_p;

if (cd_usuario_plano_w IS NOT NULL AND cd_usuario_plano_w::text <> '') then
	update	PTU_NOTA_COBRANCA
	set	CD_USUARIO_PLANO	= substr(cd_usuario_plano_w,5,17),
		CD_UNIMED		= substr(cd_usuario_plano_w,1,4)
	where	nr_sequencia		= nr_seq_nota_cobr_p;

	CALL PTU_GERAR_INCO_RECEB_A700_PCK.ptu_gerar_inconsistencias(null,nr_seq_nota_cobr_p,nm_usuario_p,cd_estabelecimento_p);
end if;

commit;

end;
$body$
