SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qep_gerar_respostas_imagem ( nr_seq_que_questao_p bigint, nr_seq_imagem_banco_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_questao_imagem_w	bigint;

BEGIN

select	count(1)
into STRICT	qt_questao_imagem_w
from	QUE_QUESTAO
where	nr_sequencia = nr_seq_que_questao_p
and	IE_TIPO_RESPOSTA = 'I'
and	(NR_SEQ_IMAGEM_BANCO IS NOT NULL AND NR_SEQ_IMAGEM_BANCO::text <> '')  LIMIT 1;

if (qt_questao_imagem_w > 0) then
	-- Caso a imagem da questão for trocada,  e existam registros com áreas da imagem antiga, remove estes registros antes de adicionar as novas imagens.
	delete
	from	QUE_QUESTAO_OPCAO
	where	nr_seq_questao = nr_seq_que_questao_p
	and (coalesce(nr_seq_area_imagem::text, '') = ''
	or	nr_seq_area_imagem not in (	SELECT	nr_sequencia
						from	area_imagem
						where	nr_seq_img = nr_seq_imagem_banco_p));

	if (nr_seq_imagem_banco_p IS NOT NULL AND nr_seq_imagem_banco_p::text <> '') then
		insert into que_questao_opcao(
			nr_sequencia,
			nr_seq_questao,
			ds_opcao,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_situacao,
			nr_seq_area_imagem)
		SELECT	nextval('que_questao_opcao_seq'),
			nr_seq_que_questao_p,
			trim(both a.ds_titulo),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'A',
			a.nr_sequencia
		from	area_imagem a
		where	a.nr_seq_img = nr_seq_imagem_banco_p
		and not exists (	SELECT	1
					from	que_questao_opcao x
					where	x.nr_seq_area_imagem = a.nr_sequencia);
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
