CREATE OR REPLACE PROCEDURE qsm_obter_modulos () AS $body$
DECLARE


	reg_integracao_p		gerar_int_padrao.reg_integracao;
	qt_reg_w				varchar(10);
	

BEGIN

	select	count(1)
    into STRICT qt_reg_w
    from intpd_fila_transmissao a,
		 intpd_eventos_sistema b
    where a.nr_seq_evento_sistema = b.nr_sequencia
    and	a.ie_evento = '409'
    and	b.ds_action like 'http://www.mmm.com/his/qsmed/common/getModules';
			
	reg_integracao_p => reg_integracao_p := gerar_int_padrao.gravar_integracao(ie_evento_p => '409', nr_seq_documento_p => qt_reg_w, nm_usuario_p => 'WebService', reg_integracao_p => reg_integracao_p, ds_parametros_adic_p => null);
	
end;
