CREATE OR REPLACE PROCEDURE qt_alertar_sus_apac (nr_apac_sus_p text, nr_seq_paciente_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_consistencia_p INOUT text) AS $body$
DECLARE

 
cd_convenio_sus_w	bigint;			
cd_convenio_w		bigint;
			

BEGIN 
 
select	max(cd_convenio_sus) 
into STRICT	cd_convenio_sus_w 
from	parametro_faturamento 
where	cd_estabelecimento = cd_estabelecimento_p;
 
if (cd_convenio_sus_w IS NOT NULL AND cd_convenio_sus_w::text <> '') then 
	select	max(cd_convenio) 
	into STRICT	cd_convenio_w 
	from	paciente_setor_convenio 
	where	nr_seq_paciente = nr_seq_paciente_p;
	 
	if (cd_convenio_w = cd_convenio_sus_w) and (coalesce(nr_apac_sus_p::text, '') = '') then 
		ds_consistencia_p := wheb_mensagem_pck.get_texto(302068);
	end if;	
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
