CREATE OR REPLACE PROCEDURE qt_alterar_medico_resp ( cd_medico_resp_p text, nr_seq_paciente_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		varchar(10);
cd_medico_resp_w	varchar(10);
				

BEGIN 
 
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_medico_resp_p IS NOT NULL AND cd_medico_resp_p::text <> '')then 
   
  select	max(cd_medico_resp) 
  into STRICT	cd_medico_resp_w 
  from	paciente_setor 
  where	nr_seq_paciente = nr_seq_paciente_p;
   
  update 	PACIENTE_SETOR 
  set   	nm_usuario   =  nm_usuario_p, 
		dt_atualizacao =  clock_timestamp(), 
		cd_medico_resp = cd_medico_resp_p 
  where  	nr_seq_paciente = nr_seq_paciente_p;
   
  select	max(nr_sequencia) 
  into STRICT	nr_sequencia_w 
  from	motivo_alteracao_prot_onc 
  where	ie_medic_resp_quimio = 'S';
   
  if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then 
	CALL Gravar_Motivo_Alt_Prot_Onc(	nr_seq_paciente_p,null,null,nr_sequencia_w,wheb_mensagem_pck.get_texto(455963), 
					Obter_Pf_Usuario(nm_usuario_p,'C'),'A', nm_usuario_p,'PACIENTE_SETOR', 'CD_MEDICO_RESP', 
					cd_medico_resp_w,cd_medico_resp_p,null);	
  end if;
   
end if;
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
