SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_atualizar_conf_ordem (nr_seq_ordem_p bigint, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_perm_nao_liberado_w		varchar(1);
ie_liberado_w				varchar(1) := 'S';

BEGIN

ie_perm_nao_liberado_w := Obter_param_Usuario(3130, 242, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_perm_nao_liberado_w);

if (ie_perm_nao_liberado_w = 'N') then
	select  coalesce(max('S'),'N')
	into STRICT 	ie_liberado_w
    from    prescr_medica
    where   nr_prescricao = nr_prescricao_p
	and     ((dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') or (dt_liberacao_medico IS NOT NULL AND dt_liberacao_medico::text <> ''));
end if;

if (ie_liberado_w = 'S') then
	update	can_ordem_prod
	set		ie_conferido 	=	'S',
			nm_usuario		=	nm_usuario_p,
			dt_atualizacao	=	clock_timestamp()
	where 	nr_sequencia 	=	nr_seq_ordem_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
