SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_atualizar_data_chegada_trat ( ds_lista_dias_trat_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_atendimento_w	bigint;
nr_atendimento_w	bigint;
ds_lista_w		varchar(1000);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
nr_seq_status_w		bigint;



BEGIN

ds_lista_w 		:= ds_lista_dias_trat_p;

if (substr(ds_lista_w,length(ds_lista_w) - 1, length(ds_lista_w))	<> ',') then
	ds_lista_w	:= ds_lista_w ||',';
end if;

while(ds_lista_w IS NOT NULL AND ds_lista_w::text <> '') loop
	begin

	tam_lista_w		:= length(ds_lista_w);
	ie_pos_virgula_w	:= position(',' in ds_lista_w);

	if (ie_pos_virgula_w <> 0) then
		nr_seq_atendimento_w	:= (substr(ds_lista_w,1,(ie_pos_virgula_w - 1)))::numeric;
		ds_lista_w		:= substr(ds_lista_w,(ie_pos_virgula_w + 1),tam_lista_w);
	end if;

	update	paciente_atendimento
	set	dt_chegada		= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_seq_atendimento	= nr_seq_atendimento_w;

	end;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
