SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_atualizar_historico_transf (nr_seq_agequi_p bigint, cd_motivo_p bigint, ds_historico_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_historico_w	varchar(255);
ds_motivo_w		varchar(255);


BEGIN

if (nr_seq_agequi_p > 0) then

	select 	substr(max(ds_motivo),1,255)
	into STRICT	ds_motivo_w
	from	agenda_motivo
	where 	nr_sequencia = cd_motivo_p;

	select  max(nr_sequencia),
		substr(max(ds_historico),1,255)
	into STRICT	nr_sequencia_w,
		ds_historico_w
	from	agenda_quimio_hist
	where	nr_seq_agequi = nr_seq_agequi_p;

	if (nr_sequencia_w > 0) then
		update	agenda_quimio_hist
		set	ds_historico 	= ds_historico||', '|| wheb_mensagem_pck.get_texto(795984) || ' ' ||ds_motivo_w||' - '||ds_historico_p||', '|| wheb_mensagem_pck.get_texto(795988) || ' '||nm_usuario_p ,
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_w;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
