CREATE OR REPLACE PROCEDURE qt_atualizar_proc_apoio ( nr_seq_atendimento_p bigint, ds_lista_proc_p text, nr_seq_local_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_procedimento_w	qt_procedimento_apoio.cd_procedimento%type;
ie_origem_proced_w	qt_procedimento_apoio.ie_origem_proced%type;
qt_tempo_agenda_w	qt_procedimento_apoio.qt_tempo_agenda%type;
qt_tempo_agenda_ww	qt_procedimento_apoio.qt_tempo_agenda%type;
nr_seq_pend_agenda_w paciente_atendimento.nr_seq_pend_agenda%type;

nr_agrupamento_w	bigint := 0;

c01 CURSOR FOR
SELECT 	cd_procedimento,
		ie_origem_proced,
		qt_tempo_agenda
from 	qt_procedimento_apoio
where to_char(nr_sequencia) in (ds_lista_proc_p);			


BEGIN
qt_tempo_agenda_ww := 0;


open C01;
loop
fetch C01 into	
	cd_procedimento_w,
	ie_origem_proced_w,
	qt_tempo_agenda_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_tempo_agenda_ww := qt_tempo_agenda_ww + qt_tempo_agenda_w;
	nr_agrupamento_w := nr_agrupamento_w + 1;
	
	insert into paciente_atend_proc(
		nr_seq_procedimento,
		nr_seq_atendimento,
		cd_procedimento, 
		ie_origem_proced, 
		qt_procedimento, 
		dt_atualizacao, 
		nm_usuario, 
		nr_agrupamento
	) values (nr_agrupamento_w,
		nr_seq_atendimento_p,
		cd_procedimento_w,
		ie_origem_proced_w,
		1,
		clock_timestamp(),
		nm_usuario_p,
		nr_agrupamento_w);

	
	end;
end loop;
close C01;

select	max(nr_seq_pend_agenda)
into STRICT	nr_seq_pend_agenda_w
from	paciente_atendimento
where	nr_seq_atendimento 	= nr_seq_atendimento_p;

update	paciente_atendimento
set		nr_seq_local 		= nr_seq_local_p, 	
		nm_usuario			= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
where	nr_seq_atendimento	= nr_seq_atendimento_p;

update	agenda_quimio
set		nr_seq_local 		= nr_seq_local_p,
		nr_minuto_duracao	= qt_tempo_agenda_ww, 	
		nm_usuario			= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
where	nr_seq_atendimento	= nr_seq_atendimento_p;


update	agenda_quimio_marcacao
set		nr_duracao		=	qt_tempo_agenda_ww,
		nr_seq_local 	=	nr_seq_local_p 
where	nr_seq_pend_agenda	= nr_seq_atendimento_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
