CREATE OR REPLACE PROCEDURE qt_calcula_dose_manipulacao ( qt_dose_prescr_ml_p bigint, qt_dose_atual_ml_p bigint, qt_dose_total_ml_p bigint, cd_material_p text, qt_saldo_atual_ml_p INOUT bigint, qt_saldo_consumo_p INOUT bigint) AS $body$
DECLARE


cd_unidade_medida_w	varchar(15);
qt_dose_saldo_ml_w		double precision;


BEGIN
qt_saldo_consumo_p := 0;
qt_saldo_atual_ml_p := 0;

select 	max(coalesce(cd_unidade_medida_consumo,''))
into STRICT	cd_unidade_medida_w
from	material
where	cd_material 	=	cd_material_p;

qt_dose_saldo_ml_w :=   qt_dose_atual_ml_p + qt_dose_total_ml_p;

if (qt_dose_saldo_ml_w > qt_dose_prescr_ml_p) then
	qt_dose_saldo_ml_w := qt_dose_total_ml_p - (qt_dose_saldo_ml_w - qt_dose_prescr_ml_p);
else
	qt_dose_saldo_ml_w := qt_dose_total_ml_p;
end if;

qt_saldo_consumo_p  := Obter_dose_convertida_quimio(cd_material_p,qt_dose_saldo_ml_w,(OBTER_UNID_MED_USUA('ml')),cd_unidade_medida_w);

qt_saldo_atual_ml_p := Obter_dose_convertida_quimio(cd_material_p,qt_saldo_consumo_p,cd_unidade_medida_w,(OBTER_UNID_MED_USUA('ml'))) + qt_dose_atual_ml_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
