SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_confirmar_marcacao_ageint ( nr_seq_ageint_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_atendimento_w	bigint;
nr_seq_paciente_w	bigint;
cd_pessoa_fisica_w	varchar(10);
dt_agenda_w		timestamp;
nr_seq_local_w		bigint;
nr_duracao_w		bigint;
nr_seq_marcacao_w	bigint;
nr_seq_prof_W		bigint;
ie_tipo_agendamento_w	varchar(15);
nr_ciclo_w		smallint;
ie_gera_autorizacao_w	varchar(1);
cd_estabelecimento_w	smallint;
nr_seq_Agenda_w		bigint;
ie_tipo_pend_agenda_w	varchar(15);
nr_seq_ageint_item_w	bigint;
nr_seq_pend_quimio_w	bigint;
ie_anestesia_w		varchar(1);
ie_encaixe_w		varchar(1);
cd_estab_local_w    smallint;
			
C01 CURSOR FOR
	SELECT	a.nr_seq_local,
		a.dt_agenda,
		a.ie_tipo_agendamento,
		a.nr_duracao,
		a.nr_sequencia,
		c.cd_pessoa_fisica,
		b.ie_tipo_pend_agenda,
		b.nr_sequencia,
		a.nr_seq_prof,
		b.nr_seq_pend_quimio,
		coalesce(b.ie_anestesia,'N'),
		coalesce(a.ie_encaixe,'N')
	from	agenda_quimio_marcacao a,
		agenda_integrada_item b,
		agenda_integrada c
	where	a.nr_seq_ageint_item	= b.nr_sequencia
	and	b.nr_seq_agenda_int	= nr_Seq_Ageint_p
	and	b.nr_seq_Agenda_int	= c.nr_sequencia
	and	a.ie_gerado		= 'N';
	
			

BEGIN

ie_gera_autorizacao_w := obter_param_usuario(865, 6, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_gera_autorizacao_w);

open C01;
loop
fetch C01 into	
	nr_seq_local_w,
	dt_agenda_w,
	ie_tipo_agendamento_w,
	nr_duracao_w,
	nr_seq_marcacao_w,
	cd_pessoa_fisica_w,
	ie_tipo_pend_agenda_w,
	nr_seq_ageint_item_w,
	nr_seq_prof_w,
	nr_seq_pend_quimio_w,
	ie_anestesia_w,
	ie_encaixe_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	
	/*update	agenda_quimio
	set	nr_seq_local	= nr_seq_local_w, 
		dt_agenda	= dt_agenda_w,
		dt_atualizacao	= sysdate,
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_agenda_p;*/
		
	select	max(cd_estabelecimento)
	into STRICT	cd_estab_local_w
	from	qt_local
	where	nr_sequencia = nr_seq_local_w;
	
	if (coalesce(cd_pessoa_fisica_w::text, '') = '') then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(215701);
	end if;	
	
	select	nextval('agenda_quimio_seq')
	into STRICT	nr_seq_Agenda_w
	;
	
	insert into agenda_quimio(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_fisica,
		nr_seq_local,
		nr_minuto_duracao,
		dt_agenda,
		ie_tipo_pend_agenda,
		ie_status_agenda,
		ie_tipo_agendamento,
		cd_estabelecimento,
		nr_seq_ageint_item,
		nr_seq_prof,
		nr_seq_pend_agenda,
		ie_anestesia,
		ie_encaixe)
	values (nr_seq_Agenda_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p, 
		cd_pessoa_fisica_w,
		nr_seq_local_w,
		nr_duracao_w,
		dt_agenda_w,
		ie_tipo_pend_agenda_w,
		'N',
		ie_tipo_agendamento_w,
		cd_estabelecimento_p,
		nr_seq_ageint_item_w,
		nr_seq_prof_w,
		nr_seq_pend_quimio_w,
		ie_anestesia_w,
		ie_encaixe_w);
		
	update	agenda_quimio_marcacao
	set	ie_gerado	= 'S'
	where	nr_sequencia	= nr_seq_marcacao_w;
	
	update	agenda_integrada_item
	set		nr_seq_Agequi	= nr_seq_Agenda_w
	where	nr_sequencia	= nr_seq_ageint_item_w;
	
	if (nr_seq_ageint_p IS NOT NULL AND nr_seq_ageint_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '')then
		CALL ageint_ins_anexo_email(nr_seq_ageint_p, nm_usuario_p, cd_estab_local_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
