CREATE OR REPLACE PROCEDURE qt_confirmar_marcacao_js ( nr_seq_pend_agenda_p bigint, nr_seq_ageint_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ie_valida_agrupamento_p text default 'N') AS $body$
DECLARE


ie_atualiza_conv_w varchar(1);
nr_seq_pendencia_w		paciente_atendimento.nr_seq_pend_agenda%type;

C01 CURSOR FOR
	SELECT DISTINCT b.nr_seq_pend_agenda
	FROM paciente_setor a,
		paciente_atendimento b
	WHERE a.nr_seq_paciente	= b.nr_seq_paciente AND
		(b.nr_seq_pend_agenda = nr_seq_pend_agenda_p OR
			(a.ie_agendamento_multiplo = 'S' AND
				b.nr_seq_pend_agenda <> nr_seq_pend_agenda_p AND
				ie_valida_agrupamento_p = 'S' AND
				a.nr_grupo_agendamento = (SELECT MAX(wp.nr_grupo_agendamento)
											FROM w_pendencia_agequi wp
											WHERE wp.nr_seq_pendencia = nr_seq_pend_agenda_p
												AND (wp.nr_grupo_agendamento IS NOT NULL AND wp.nr_grupo_agendamento::text <> '')) AND
				a.cd_pessoa_fisica = (SELECT MAX(wp.cd_pessoa_fisica)
											FROM w_pendencia_agequi wp
											WHERE wp.nr_seq_pendencia = nr_seq_pend_agenda_p
												AND (wp.nr_grupo_agendamento IS NOT NULL AND wp.nr_grupo_agendamento::text <> ''))))
	ORDER BY b.nr_seq_pend_agenda ASC;


BEGIN
	ie_atualiza_conv_w := obter_param_usuario(869, 426, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_atualiza_conv_w);

	open C01;
	loop
	fetch C01 into
		nr_seq_pendencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		if (nr_seq_pendencia_w <> 0) then
			begin
				if (coalesce(nr_seq_ageint_p,0) > 0) and (ie_atualiza_conv_w = 'S') then
					CALL ageint_atualiza_conv_quimio(nr_seq_pendencia_w, nr_seq_ageint_p);
				end if;
				
				CALL qt_confirmar_marcacao(nr_seq_pendencia_w, nm_usuario_p, null, cd_estabelecimento_p);
			end;
		end if;
	end;
	end loop;
	close C01;

	CALL qt_confirmar_marcacao_ageint(nr_seq_ageint_p, nm_usuario_p, cd_estabelecimento_p);
	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
