CREATE OR REPLACE PROCEDURE qt_confirmar_trat ( nr_sequencia_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then

	if (ds_observacao_p IS NOT NULL AND ds_observacao_p::text <> '') then

		update	agenda_quimio
		set	dt_confirmacao	= clock_timestamp(),
			ds_observacao	= ds_observacao_p,
			dt_atualizacao  = clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_p;

	else

		update	agenda_quimio
		set	dt_confirmacao	= clock_timestamp(),
			dt_atualizacao  = clock_timestamp(),
			nm_usuario	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_p;

	end if;

end if;
commit;

end;
$body$
