CREATE OR REPLACE PROCEDURE qt_consiste_confirmacao ( nr_seq_pend_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, qt_dias_pend_p INOUT bigint, qt_dias_marcados_p INOUT bigint) AS $body$
DECLARE


qt_dias_pend_w		bigint;
qt_dias_marcados_w	bigint;
ie_adm_final_w		varchar(1);


BEGIN

ie_adm_final_w	:=	Obter_Valor_Param_Usuario(865, 104, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p);

if (ie_adm_final_w	= 'N') then
	select	count(*)
	into STRICT	qt_dias_pend_w
	from 	paciente_atendimento a
	where 	coalesce(dt_cancelamento::text, '') = ''
	and	coalesce(dt_interrompido::text, '') = ''
	and	coalesce(dt_suspensao_medico::text, '') = ''
	and	coalesce(dt_fim_adm::text, '') = ''
	and 	nr_seq_pend_agenda	= nr_seq_pend_agenda_p
	and not exists (SELECT 1 from agenda_quimio b where b.nr_seq_Atendimento = b.nr_seq_atendimento and ie_status_agenda in ('S','C','F'));
else
	select	count(*)
	into STRICT	qt_dias_pend_w
	from 	paciente_atendimento
	where 	coalesce(dt_cancelamento::text, '') = ''
	and 	nr_seq_pend_agenda	= nr_seq_pend_agenda_p;
end if;
select 	count(*)
into STRICT	qt_dias_marcados_w
from 	agenda_quimio_marcacao a
where 	a.nr_seq_pend_agenda	= nr_seq_pend_agenda_p
and	not exists (SELECT 1
		from	agenda_quimio b
		where	b.nr_seq_atendimento	= a.nr_seq_atendimento
		and	b.ie_status_agenda in ('C','F','S'));

qt_dias_pend_p		:= qt_dias_pend_w;
qt_dias_marcados_p	:= qt_dias_marcados_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
