CREATE OR REPLACE PROCEDURE qt_consistir_agendamento_bloq (nr_seq_local_p bigint, dt_inicial_p timestamp, dt_final_p timestamp, hr_inicial_p text, hr_final_p text, nm_usuario_p text, cd_estabelecimento_p bigint, ie_agendamento_p INOUT text) AS $body$
DECLARE


qt_agendamento_w	bigint;
dt_inicio_w		timestamp;
dt_fim_w		timestamp;

BEGIN
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then

	dt_inicio_w := to_date(to_char(dt_inicial_p,'dd/mm/yyyy') || ' ' || coalesce(hr_inicial_p,'00:00:01'),'dd/mm/yyyy hh24:mi:ss');

	dt_fim_w := to_date(to_char(dt_final_p,'dd/mm/yyyy') || ' ' || coalesce(hr_final_p,'23:59:59'),'dd/mm/yyyy hh24:mi:ss');

	select	count(*)
	into STRICT	qt_agendamento_w
	from	agenda_quimio
	where	ie_status_agenda not in ('C','F')
	and	dt_agenda between dt_inicio_w and dt_fim_w
	and	((nr_seq_local = nr_seq_local_p) or (coalesce(nr_seq_local_p::text, '') = ''));

	if (qt_agendamento_w > 0) then
		ie_agendamento_p := 'S';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
