SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_consistir_excl_medic (cd_material_p bigint, ds_regra_p INOUT text) AS $body$
DECLARE


nr_seq_regra_w			bigint;
cd_classe_material_w	integer;
cd_grupo_material_w		smallint;
cd_subgrupo_material_w	smallint;
ie_permite_exclusao_w	varchar(1);

BEGIN
ds_regra_p 		:= '';
nr_seq_regra_w 	:= 0;

select	coalesce(max(a.cd_classe_material),0),
		coalesce(max(b.cd_subgrupo_material),0),
		coalesce(max(c.cd_grupo_material),0)
into STRICT	cd_classe_material_w,
		cd_subgrupo_material_w,
		cd_grupo_material_w
from	material a,
		classe_material b,
		subgrupo_material c
where	a.cd_material          = cd_material_p
and 	a.cd_classe_material   = b.cd_classe_material
and 	b.cd_subgrupo_material = c.cd_subgrupo_material;

if (cd_material_p > 0) then
	select 	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_regra_w
	from 	regra_quimio_exclusao_med
	where 	cd_material	 		 			= cd_material_p;
end if;

if (nr_seq_regra_w = 0)	then
    select 	coalesce(max(nr_sequencia),0)
    into STRICT	nr_seq_regra_w
    from 	regra_quimio_exclusao_med
    where 	cd_classe_material	= cd_classe_material_w;
end if;

if (nr_seq_regra_w = 0)	then
    select 	coalesce(max(nr_sequencia),0)
    into STRICT	nr_seq_regra_w
    from 	regra_quimio_exclusao_med
    where 	cd_subgrupo_material	= cd_subgrupo_material_w;
end if;

if (nr_seq_regra_w = 0)	then
    select 	coalesce(max(nr_sequencia),0)
    into STRICT	nr_seq_regra_w
    from 	regra_quimio_exclusao_med
    where 	cd_grupo_material 		= cd_grupo_material_w;
end if;

if (nr_seq_regra_w > 0) then
	select 	coalesce(max(ie_permite_exclusao),'N')
	into STRICT	ie_permite_exclusao_w
	from	regra_quimio_exclusao_med
	where	nr_sequencia = 	nr_seq_regra_w;

	if (ie_permite_exclusao_w = 'N') then
		select 	max(ds_regra)
		into STRICT	ds_regra_p
		from	regra_quimio_exclusao_med
		where	nr_sequencia = 	nr_seq_regra_w;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
