CREATE OR REPLACE PROCEDURE qt_consistir_horario (dt_agenda_p timestamp, nr_seq_local_p bigint, nr_min_duracao_p bigint, nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_retorno_w	bigint;			
			       

BEGIN
if (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') and (nr_min_duracao_p IS NOT NULL AND nr_min_duracao_p::text <> '') then
	begin	
	
		
	select	1
	into STRICT	qt_retorno_w
	from	agenda_quimio
	where	((dt_agenda_p between dt_agenda and dt_agenda + (nr_minuto_duracao - 1) / 1440 )
	or	(dt_agenda_p + (nr_min_duracao_p - 1) / 1440 between dt_agenda and dt_agenda + (nr_minuto_duracao - 1) / 1440)
	or (dt_agenda between dt_agenda_p and  dt_agenda_p + (nr_min_duracao_p - 1) / 1440))
	and	nr_seq_local = nr_seq_local_p 
	and	ie_status_agenda not in ('C','F')
	and	nr_sequencia <> nr_seq_agenda_p  LIMIT 1;
	exception
	when others then
		qt_retorno_w := 0;
	end;

	if (coalesce(qt_retorno_w,0) > 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(262524);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
