SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_excluir_pendencia_trat ( nr_seq_paciente_p bigint, nr_seq_Atendimento_p bigint, dt_prevista_p timestamp, dt_real_p timestamp, nr_seq_pend_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
qt_dia_Trat_Agendado_w		bigint;
qt_itens_pendencia_w		bigint;
ie_excluir_pend_agenda_w 	varchar(255);
			

BEGIN 
if (nr_seq_pend_Agenda_p IS NOT NULL AND nr_seq_pend_Agenda_p::text <> '') then 
	select	count(*) 
	into STRICT	qt_dia_trat_agendado_w 
	from	agenda_quimio 
	where	nr_seq_Atendimento	= nr_seq_Atendimento_p 
	and	coalesce(ie_status_agenda,'N') <> 'C';
 
	if (qt_dia_trat_agendado_w	> 0) then 
		update	agenda_quimio 
		set	nr_seq_atendimento	 = NULL, 
			ie_status_agenda	= 'C', 
			dt_cancelada		= clock_timestamp(), 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp(), 
			nm_usuario_cancel	= nm_usuario_p, 
			ds_motivo_status    = obter_desc_expressao(781887) --'Exclus√£o do dia de tratamento' 
		where	nr_seq_atendimento	= nr_seq_atendimento_p;
 
    ie_excluir_pend_agenda_w := Obter_Valor_Param_Usuario(281, 859, Obter_perfil_Ativo, Wheb_Usuario_Pck.Get_nm_Usuario, Wheb_Usuario_Pck.get_cd_estabelecimento);
    if ( ie_excluir_pend_agenda_w = 'S') then 
      CALL qt_enviar_ci_exclusao_trat(nr_seq_atendimento_p, coalesce(dt_prevista_p, dt_real_p), nm_usuario_p);
    end if;
	end if;
 
	select	count(*) 
	into STRICT	qt_itens_pendencia_w 
	from	paciente_atendimento 
	where	nr_seq_pend_agenda	= nr_seq_pend_Agenda_p 
	and	nr_seq_atendimento	<> nr_seq_Atendimento_p;
	if (qt_itens_pendencia_w	= 0) then 
		delete	FROM agenda_quimio_marcacao 
		where	nr_seq_pend_agenda	= nr_seq_pend_agenda_p;
		 
		update	paciente_atendimento 
		set	nr_seq_pend_Agenda	 = NULL 
		where	nr_seq_atendimento	= nr_seq_atendimento_p;
		 
		delete FROM w_agenda_quimio 
		where nr_seq_pend_agenda = nr_seq_pend_agenda_p;
		 
		delete	FROM qt_pendencia_agenda 
		where	nr_sequencia	= nr_seq_pend_agenda_p;
	end if;
end if;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
