SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_gerar_encaixe_quimio_dt ( hr_agendamento_p timestamp, nr_Seq_local_p bigint, qt_tempo_medic_p bigint, dt_agendamento_p timestamp, nr_seq_ageint_item_p bigint, nr_Seq_prof_p bigint, nm_usuario_p text, ds_observacao_p text, cd_estabelecimento_p bigint, ie_transferencia_p text) AS $body$
DECLARE


dt_Agenda_w		timestamp;
nr_seq_pend_quimio_w	bigint;
ie_tipo_agendamento_w	varchar(15);
qt_item_recorrencia_w	bigint;
dt_prevista_quimio_w	timestamp;
nr_seq_ageint_item_w	bigint;
nr_seq_Atendimento_w	bigint;

C01 CURSOR FOR
	SELECT	a.dt_prevista_quimio,
		a.nr_sequencia
	from	agenda_integrada_item a
	where	a.nr_seq_item_princ = nr_seq_ageint_item_p
	and	not exists (SELECT 1 from agenda_quimio_marcacao x where x.nr_seq_ageint_item	= a.nr_sequencia)
	order by 1;


BEGIN

begin
select	1
into STRICT	qt_item_recorrencia_w
from	agenda_integrada_item
where	nr_seq_item_princ = nr_seq_ageint_item_p  LIMIT 1;
exception
when others then
	qt_item_recorrencia_w	:= 0;
end;


select	max(nr_seq_pend_quimio),
	max(ie_tipo_agendamento)
into STRICT	nr_seq_pend_quimio_w,
	ie_tipo_agendamento_w
from	agenda_integrada_item
where	nr_sequencia = nr_seq_ageint_item_p;

dt_agenda_w	:= PKG_DATE_UTILS.get_DateTime(dt_Agendamento_p, hr_agendamento_p);

if (coalesce(nr_seq_pend_quimio_w,0) > 0) then
	select	max(nr_seq_atendimento)
	into STRICT	nr_seq_Atendimento_w
	from	paciente_atendimento
	where	nr_seq_pend_agenda	= nr_seq_pend_quimio_w
	and		trunc(coalesce(dt_real, dt_prevista))	= trunc(dt_agenda_w);
end if;

insert into agenda_quimio_marcacao(nr_sequencia,
	dt_agenda,
	nm_usuario,
	nr_seq_local,
	nr_duracao,
	nr_seq_pend_agenda,
	dt_atualizacao,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ie_gerado,
	ie_transferencia,
	nr_seq_prof,
	ds_observacao,
	ie_tipo_agendamento,
	nr_seq_Ageint_item,
	ie_encaixe,
	nr_seq_Atendimento)
values (nextval('agenda_quimio_marcacao_seq'),
	dt_agenda_w,
	nm_usuario_p,
	nr_seq_local_p,
	qt_tempo_medic_p,
	nr_seq_pend_quimio_w,
	clock_timestamp(),
	clock_timestamp(),
	nm_usuario_p,
	'N',
	ie_transferencia_p,
	nr_seq_prof_p,
	ds_observacao_p,
	ie_tipo_agendamento_w,
	nr_seq_ageint_item_p,
	'S',
	nr_seq_Atendimento_w);

if (qt_item_recorrencia_w > 0) then
	open C01;
	loop
	fetch C01 into
		dt_prevista_quimio_w,
		nr_seq_ageint_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	max(nr_seq_pend_quimio),
			max(ie_tipo_agendamento)
		into STRICT	nr_seq_pend_quimio_w,
			ie_tipo_agendamento_w
		from	agenda_integrada_item
		where	nr_sequencia = nr_seq_ageint_item_w;

		dt_agenda_w	:= PKG_DATE_UTILS.get_DateTime(dt_prevista_quimio_w, hr_agendamento_p);

		insert into agenda_quimio_marcacao(nr_sequencia,
			dt_agenda,
			nm_usuario,
			nr_seq_local,
			nr_duracao,
			nr_seq_pend_agenda,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_gerado,
			ie_transferencia,
			nr_seq_prof,
			ds_observacao,
			ie_tipo_agendamento,
			nr_seq_Ageint_item,
			ie_encaixe)
		values (nextval('agenda_quimio_marcacao_seq'),
			dt_agenda_w,
			nm_usuario_p,
			nr_seq_local_p,
			qt_tempo_medic_p,
			nr_seq_pend_quimio_w,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			'N',
			ie_transferencia_p,
			nr_seq_prof_p,
			ds_observacao_p,
			ie_tipo_agendamento_w,
			nr_seq_ageint_item_w,
			'S');
		end;
	end loop;
	close C01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE qt_gerar_encaixe_quimio_dt ( hr_agendamento_p timestamp, nr_Seq_local_p bigint, qt_tempo_medic_p bigint, dt_agendamento_p timestamp, nr_seq_ageint_item_p bigint, nr_Seq_prof_p bigint, nm_usuario_p text, ds_observacao_p text, cd_estabelecimento_p bigint, ie_transferencia_p text) FROM PUBLIC;

