CREATE OR REPLACE PROCEDURE qt_grava_alt_protocolo_medic ( nr_seq_paciente_p bigint) AS $body$
DECLARE


nr_seq_pend_agenda_w	bigint;

BEGIN

if (coalesce(nr_seq_paciente_p,0) > 0) then
	select	max(nr_seq_pend_agenda)
	into STRICT	nr_seq_pend_agenda_w
	from	paciente_atendimento
	where	nr_seq_paciente = nr_seq_paciente_p;

	if (coalesce(nr_seq_pend_agenda_w,0) > 0) then
		update	qt_pendencia_agenda
		set	ie_alt_medicamento = 'S'
		where	nr_sequencia = nr_seq_pend_agenda_w;
	end if;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
