CREATE OR REPLACE PROCEDURE qt_inserir_bloqueio_local ( nr_seq_local_p bigint, dt_agenda_p timestamp, hr_inicio_p timestamp, hr_fim_p timestamp, ds_observacao_p text, nm_usuario_p text ) AS $body$
BEGIN

IF (nr_seq_local_p IS NOT NULL AND nr_seq_local_p::text <> '') THEN

    INSERT INTO qt_bloqueio(
        nr_sequencia,
        nr_seq_local,
        dt_atualizacao,
        nm_usuario,
        dt_atualizacao_nrec,
        nm_usuario_nrec,
        dt_inicial,
        dt_final,
        hr_inicio_bloqueio,
        hr_final_bloqueio,
        ds_observacao,
        dt_dia_semana
    ) VALUES (
        nextval('qt_bloqueio_seq'),
        nr_seq_local_p,
        clock_timestamp(),
        nm_usuario_p,
        clock_timestamp(),
        nm_usuario_p,
        TRUNC(dt_agenda_p),
        fim_dia(dt_agenda_p),
        hr_inicio_p,
        hr_fim_p,
        ds_observacao_p,
        NULL
    );
END IF;

COMMIT;

END;
