CREATE OR REPLACE PROCEDURE qt_inserir_protocolos_grupo ( ds_protocolos_p text, nr_seq_grupo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


tam_lista_w		bigint;
ie_pos_virgula_w		smallint	:= 0;
ds_lista_grupo_w		varchar(800);
ds_protocolos_w		varchar(800);
cd_item_w		varchar(3);
nm_protocolo_w		varchar(255);
nr_seq_item_w		bigint;


BEGIN

ds_protocolos_w	:= replace(ds_protocolos_p,',','');

select 	coalesce(max(somente_numero(cd_item)),0)
into STRICT	nr_seq_item_w
from	qt_item_agenda;

if (nr_seq_item_w >= 999) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(247883);
else
	cd_item_w := nr_seq_item_w + 1;
end if;

select	MAX(nm_protocolo)
into STRICT	nm_protocolo_w
from	protocolo
where 	cd_protocolo = ds_protocolos_w;

insert into qt_item_agenda(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_item,
			nr_seq_grupo,
			cd_item,
			cd_protocolo)
		values (
			nextval('qt_item_agenda_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nm_protocolo_w,
			nr_seq_grupo_p,
			cd_item_w,
			ds_protocolos_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
