SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_limpar_dados_marcacao ( nm_usuario_p text) AS $body$
BEGIN

delete	FROM agenda_quimio_marcacao
where	nm_usuario	= nm_usuario_p
and	ie_gerado	= 'N';

delete FROM w_agenda_quimio
where	nm_usuario	= nm_usuario_p
and	trunc(dt_horario) between trunc(clock_timestamp()) and trunc(clock_timestamp()) + 7;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
