SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_marcar_medicacao_pac (nr_sequencia_p can_ordem_item_prescr.nr_sequencia%type) AS $body$
DECLARE


nr_prescricao_w can_ordem_item_prescr.nr_prescricao%type;
cd_material_w can_ordem_item_prescr.cd_material%type;
nr_seq_prescricao_w can_ordem_item_prescr.nr_seq_prescricao%type;
qt_registros_w bigint;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

    select  nr_prescricao,
            cd_material,
            nr_seq_prescricao
    into STRICT    nr_prescricao_w,
            cd_material_w,
            nr_seq_prescricao_w
    from    can_ordem_item_prescr
    where   nr_sequencia = nr_sequencia_p;

    select  count(*)
    into STRICT    qt_registros_w
    from    prescr_material
    where   nr_prescricao = nr_prescricao_w
    and     cd_material = cd_material_w
    and     nr_sequencia = nr_seq_prescricao_w;

    if (qt_registros_w = 1) then

        update  prescr_material
        set     ie_medicacao_paciente = 'S'
        where   nr_prescricao = nr_prescricao_w
        and     cd_material = cd_material_w
        and     nr_sequencia = nr_seq_prescricao_w;
    else
        CALL wheb_mensagem_pck.exibir_mensagem_abort(58526);
    end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
