SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qt_verifica_saldo_material (nr_seq_paciente_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


cd_local_estoque_w	bigint;
ie_estoque_disp_w	varchar(1) := 'S';
qt_dose_w		bigint;
cd_material_w		bigint;
ds_erro_w		varchar(255);
ie_consignado_w		varchar(1);
ie_permite_w		varchar(1);
cd_material_gererico_w	integer;

C01 CURSOR FOR
	SELECT	cd_material,
			qt_dose
	from	paciente_atend_medic
	where	nr_seq_paciente = nr_seq_paciente_p
	order by 1;

BEGIN
cd_local_estoque_w := obter_param_usuario(3130, 191, Obter_perfil_Ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, cd_local_estoque_w);

open C01;
loop
fetch C01 into
	cd_material_w,
	qt_dose_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(max(ie_consignado),'0')
	into STRICT	ie_consignado_w
	from	material
	where	cd_material = cd_material_w;

	if (ie_consignado_w <> '0') then
		ie_permite_w	:= 'S';
	else
		ie_permite_w	:= 'N';
	end if;

	select	max(cd_material_generico)
	into STRICT	cd_material_gererico_w
	from	material
	where	cd_material = cd_material_w;

	if (ie_permite_w = 'N') then
		if (cd_material_gererico_w IS NOT NULL AND cd_material_gererico_w::text <> '') then
			ie_estoque_disp_w := obter_disp_estoque(cd_material_gererico_w, cd_local_estoque_w, wheb_usuario_pck.get_cd_estabelecimento, 0, qt_dose_w, null, ie_estoque_disp_w);

			if (ie_estoque_disp_w = 'N') then
				ds_erro_w := substr(ds_erro_w||substr(obter_desc_material(cd_material_gererico_w),1,255)||', '||chr(13)||chr(10),1,255);
			end if;
		else
			ie_estoque_disp_w := obter_disp_estoque(cd_material_w, cd_local_estoque_w, wheb_usuario_pck.get_cd_estabelecimento, 0, qt_dose_w, null, ie_estoque_disp_w);

			if (ie_estoque_disp_w = 'N') then
				ds_erro_w := substr(ds_erro_w||substr(obter_desc_material(cd_material_w),1,255)||', '||chr(13)||chr(10),1,255);
			end if;
		end if;
	end if;

	end;
end loop;
close C01;

if (ds_erro_w IS NOT NULL AND ds_erro_w::text <> '') then

	ds_erro_p	:= substr(WHEB_MENSAGEM_PCK.get_texto(280110,null) ||chr(13)||chr(10)||ds_erro_w,1,255);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
