CREATE OR REPLACE PROCEDURE qt_vincular_prescricao (nr_seq_atendimento_p bigint, nr_atendimento_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_prescricao_w		bigint;				
				 

BEGIN

select	max(nr_prescricao)
into STRICT	nr_prescricao_w
from	paciente_atendimento
where	nr_seq_atendimento = nr_seq_atendimento_p;

if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then
	update	prescr_medica
	set	nr_atendimento 	= nr_atendimento_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_prescricao  	= nr_prescricao_w;

  CALL CPOE_Desvincular_atend_prescr(nr_prescricao_w, wheb_usuario_pck.get_nm_usuario);
  CALL CPOE_Vincular_atend_prescr(nr_prescricao_w, nr_atendimento_p, wheb_usuario_pck.get_nm_usuario);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
