CREATE OR REPLACE PROCEDURE qua_ajustar_setor_pessoa_trein () AS $body$
DECLARE


c01 CURSOR FOR
SELECT	nr_sequencia,
	cd_pessoa_fisica
from	qua_doc_trein_pessoa
where	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '')
and	coalesce(cd_setor_atendimento::text, '') = '';

nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_setor_w		bigint;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(cd_setor_atendimento)
	into STRICT	cd_setor_w
	from	usuario
	where	cd_pessoa_fisica = cd_pessoa_fisica_w;

	if (coalesce(cd_setor_w,'0') <> '0') then
		update	qua_doc_trein_pessoa
		set	cd_setor_atendimento = cd_setor_w
		where	nr_sequencia = nr_sequencia_w;
	end if;

	end;
end loop;
close C01;

commit;

end;
