SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qua_alterar_nome_documento ( nr_seq_documento_p bigint, nm_usuario_p text, ie_origem_p text) AS $body$
DECLARE


ie_nome_igual_w		varchar(1);
nm_documento_w		varchar(80);
nm_OS_w			varchar(80);
nr_seq_ordem_w		bigint;


BEGIN
if (nr_seq_documento_p IS NOT NULL AND nr_seq_documento_p::text <> '') then
	select	max(substr(nm_documento,1,80))
	into STRICT	nm_documento_w
	from	qua_documento
	where	nr_sequencia = nr_seq_documento_p;

	select	max(substr(ds_dano_breve,1,80))
	into STRICT	nm_OS_w
	from	man_ordem_servico
	where	nr_seq_documento = nr_seq_documento_p
	and	ie_tipo_ordem = 11;

	if (nm_documento_w IS NOT NULL AND nm_documento_w::text <> '') and (nm_OS_w IS NOT NULL AND nm_OS_w::text <> '') and (nm_documento_w <> nm_OS_w) then

		if (ie_origem_p = 'O') then
			update	qua_documento
			set	nm_documento = nm_OS_w
			where	nr_sequencia = nr_seq_documento_p;

		elsif (ie_origem_p = 'Q') then
			select	nr_sequencia
			into STRICT	nr_seq_ordem_w
			from	man_ordem_servico
			where	nr_seq_documento = nr_seq_documento_p;

			update	man_ordem_servico
			set	ds_dano_breve = nm_documento_w
			where	nr_sequencia = nr_seq_ordem_w;
		end if;
	end if;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
