CREATE OR REPLACE PROCEDURE qua_atualizar_rnc_sem_acao ( qt_horas_p bigint, cd_estabelecimento_p bigint, ie_consid_tipo_p text default 'N') AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_tipo_w		bigint;
ie_causa_w		varchar(03);
ie_eficacia_w		varchar(03);
qt_causa_w		bigint;
qt_eficacia_w		bigint;

C01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.nr_seq_tipo
from	qua_nao_conformidade a
where	not exists (	SELECT	1
			from	man_ordem_servico b
			where	a.nr_sequencia = b.nr_seq_nao_conform)
and	dividir(obter_min_entre_datas(a.dt_abertura, clock_timestamp(), 1),60) > qt_horas_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(nr_seq_motivo_cancel::text, '') = ''
and	ie_status <> 'Encerrada'
and	ie_status <> 'Cancelada'
and	ie_status <> 'Sem causa'
and	ie_status <> 'Sem acao';


BEGIN

if (coalesce(qt_horas_p,0) > 0) then
	begin
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w,
		nr_seq_tipo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ie_consid_tipo_p,'N') = 'S') then
			begin
			qt_eficacia_w	:= 1;
			qt_causa_w	:= 1;
			
			select	coalesce(max(ie_causa), 'OB'),
				coalesce(max(ie_eficacia), 'OB')
			into STRICT	ie_causa_w,
				ie_eficacia_w
			from	qua_tipo_nao_conform
			where	nr_sequencia = nr_seq_tipo_w;
			
			if (ie_causa_w = 'OB') then
				select	count(*)
				into STRICT	qt_causa_w
				from	qua_nao_conform_causa
				where	nr_seq_nao_conform = nr_sequencia_w;
			end if;
			
			if (ie_eficacia_w = 'OB') then
				select	count(*)
				into STRICT	qt_eficacia_w
				from	qua_nao_conform_eficacia
				where	nr_seq_nao_conform = nr_sequencia_w;
			end if;
			
			if (qt_eficacia_w > 0) and (qt_causa_w > 0) then
				update	qua_nao_conformidade
				set	ie_status 	= 'Sem acao',
					dt_atualizacao  = clock_timestamp()
				where	nr_sequencia 	= nr_sequencia_w;
			end if;
			end;
		else
			update	qua_nao_conformidade
			set	ie_status 	= 'Sem acao'
			where	nr_sequencia 	= nr_sequencia_w;
		end if;
		end;
	end loop;
	close C01;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
