SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qua_atualizar_rnc_sem_causa ( qt_horas_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_causa_w		varchar(03);
ie_eficacia_w		varchar(03);
qt_causa_w		bigint;
qt_eficacia_w		bigint;

C01 CURSOR FOR
SELECT	a.nr_sequencia
from	qua_nao_conformidade a
where	not exists (	SELECT	1
			from	man_ordem_servico b
			where	a.nr_sequencia = b.nr_seq_nao_conform)
and	not exists (	select	1
			from	qua_nao_conform_causa c
			where	a.nr_sequencia = c.nr_seq_nao_conform)
and	substr(qua_obter_tipo_nao_conform(nr_seq_tipo,'C'),1,3) <> 'NI'
and	dividir(obter_min_entre_datas(a.dt_abertura, clock_timestamp(), 1),60) > qt_horas_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(nr_seq_motivo_cancel::text, '') = ''
and	ie_status <> 'Encerrada'
and	ie_status <> 'Cancelada'
and	ie_status <> 'Sem causa';


BEGIN

if (coalesce(qt_horas_p,0) > 0) then
	begin
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		update	qua_nao_conformidade
		set	ie_status 	= 'Sem causa'
		where	nr_sequencia 	= nr_sequencia_w;
		end;
	end loop;
	close C01;
	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
