SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qua_comunic_atraso_eficacia ( cd_estabelecimento_p bigint, ie_opcao_p text DEFAULT '') AS $body$
DECLARE

 
nr_seq_classif_w			bigint;
nr_seq_nao_conform_w			bigint;
ds_nao_conformidade_w			varchar(4000);
ds_tipo_nc_w				varchar(255);
nm_pf_abertura_w			varchar(60);
nm_pf_resp_ocorrencia_w			varchar(60);
nm_pf_resp_eficacia_w			varchar(60);
ds_titulo_w				varchar(80);
ds_comunic_w				varchar(4000);
nm_usuario_w				varchar(2000);
nr_sequencia_w				bigint;
ie_par_217_w				varchar(15);

c01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.ds_nao_conformidade, 
		substr(obter_descricao_padrao('QUA_TIPO_NAO_CONFORM','DS_TIPO_NC', a.nr_seq_tipo),1,255), 
		substr(obter_usuario_pessoa(coalesce(max(a.cd_pf_abertura),'')),1,60), 
		substr(obter_usuario_pessoa(coalesce(max(a.cd_pf_resp_ocorrencia),'')),1,60), 
		substr(obter_usuario_pessoa(coalesce(max(a.cd_pf_resp_eficacia),'')),1,60) 
	from	qua_nao_conformidade a, 
		qua_tipo_nao_conform b 
	where	a.nr_seq_tipo = b.nr_sequencia 
	and	a.cd_estabelecimento = cd_estabelecimento_p 
	and	upper(a.ie_status) = upper('eficacia') 
	and	b.ie_comunic_atraso_eficacia = 'S' 
	and	obter_dias_entre_datas(( SELECT	max(x.dt_fim_real) 
					from	man_ordem_servico x 
					where	x.nr_seq_nao_conform = a.nr_sequencia),clock_timestamp()) > a.qr_dia_analise_eficacia 
	group by	a.nr_sequencia, 
			a.ds_nao_conformidade, 
			a.nr_seq_tipo;

c02 CURSOR FOR 
	SELECT	a.nr_seq_nao_conform nr_seq_nao_conform, 
		a.nr_sequencia nr_seq_acao 
	from	man_ordem_servico a 
	where	a.ie_status_ordem = '3'	 
	and	(a.dt_fim_real IS NOT NULL AND a.dt_fim_real::text <> '') 
	and	(a.qt_dia_analise_eficacia_acao IS NOT NULL AND a.qt_dia_analise_eficacia_acao::text <> '') 
	and	((trunc(clock_timestamp()) - trunc(a.dt_fim_real) >= qt_dia_analise_eficacia_acao and ie_par_217_w = 'S') or (ie_par_217_w = 'N')) 
	and	trunc(a.dt_fim_real) + a.qt_dia_analise_eficacia_acao + 5 >= trunc(clock_timestamp());	--Até 5 dias por padrão definido pelo Anderson 
	
 
c02_w				c02%rowtype;
	
	 
	 
		 

BEGIN 
if (coalesce(ie_opcao_p,'F') = 'F') then 
	begin 
	select	obter_classif_comunic('F') 
	into STRICT	nr_seq_classif_w 
	;
 
	open c01;
	loop 
	fetch c01 into 
		nr_seq_nao_conform_w, 
		ds_nao_conformidade_w, 
		ds_tipo_nc_w, 
		nm_pf_abertura_w, 
		nm_pf_resp_ocorrencia_w, 
		nm_pf_resp_eficacia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		ds_titulo_w	:= wheb_mensagem_pck.get_texto(305329);
		/* 
		ds_comunic_w	:= substr('Não conformidade: ' || nr_seq_nao_conform_w || chr(13) || chr(10) || 
				  'Descrição: ' || ds_nao_conformidade_w || chr(13) || chr(10) || 
				  'Tipo: ' || ds_tipo_nc_w,1,4000); 
		*/
 
		ds_comunic_w	:= substr(wheb_mensagem_pck.get_texto(305332,	'NR_SEQ_NAO_CONFORM=' || nr_seq_nao_conform_w || ';' || 
										'DS_NAO_CONFORMIDADE=' || ds_nao_conformidade_w || ';' || 
										'DS_TIPO_NC=' || ds_tipo_nc_w),1,4000);
				  
		nm_usuario_w	:= nm_pf_resp_eficacia_w || ', ' || nm_pf_resp_ocorrencia_w || ', ' || nm_pf_abertura_w || ', ' || nm_usuario_w;
		 
		if (nm_usuario_w IS NOT NULL AND nm_usuario_w::text <> '') then 
			begin 
			select	nextval('comunic_interna_seq') 
			into STRICT	nr_sequencia_w 
			;
 
			insert into comunic_interna( 
				dt_comunicado, 
				ds_titulo, 
				ds_comunicado, 
				nm_usuario, 
				dt_atualizacao, 
				ie_geral, 
				nm_usuario_destino, 
				nr_sequencia, 
				ie_gerencial, 
				nr_seq_classif, 
				dt_liberacao) 
			values (clock_timestamp(), 
				ds_titulo_w, 
				ds_comunic_w, 
				'Tasy', 
				clock_timestamp(), 
				'N', 
				nm_usuario_w, 
				nr_sequencia_w, 
				'N', 
				nr_seq_classif_w, 
				clock_timestamp());
			end;
		end if;
		ds_comunic_w	:= '';
		nm_usuario_w	:= null;
		end;
	end loop;
	close c01;
	end;
	 
elsif (coalesce(ie_opcao_p,'F') = 'R') then 
	begin 
	ie_par_217_w := obter_param_usuario(4000, 217, obter_perfil_Ativo, 'Job', cd_estabelecimento_p, ie_par_217_w);
	open c02;
	loop 
	fetch c02 into	 
		c02_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin		 
		CALL qua_gerar_envio_comunicacao(c02_w.nr_seq_nao_conform, c02_w.nr_seq_acao, 'Job','37', cd_estabelecimento_p,null,null,'N');
		end;
	end loop;
	close c02;
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE qua_comunic_atraso_eficacia ( cd_estabelecimento_p bigint, ie_opcao_p text DEFAULT '') FROM PUBLIC;

