CREATE OR REPLACE PROCEDURE qua_define_eficacia ( nr_sequencia_p bigint, nm_usuario_p text, ie_eficacia_p text, nr_seq_motivo_p bigint default null) AS $body$
DECLARE


/*ie_eficacia_p
1 = Eficaz
2 = NÃ£o eficaz
3 = Desconsiderar*/
ie_eficacia_w			varchar(1);


BEGIN

if (ie_eficacia_p	= 3) then
	update	man_ordem_servico
	set	nm_usuario		= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		ie_eficacia		 = NULL,
		nr_seq_motivo_eficacia_acao  = NULL
	where	nr_sequencia		= nr_sequencia_p;
elsif (ie_eficacia_p in (1,2)) then
	begin
	ie_eficacia_w	:= 'S';
	if (ie_eficacia_p = 2) then
		ie_eficacia_w	:= 'N';
	end if;
	update	man_ordem_servico
	set	nm_usuario		= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		ie_eficacia		= ie_eficacia_w,
		nr_seq_motivo_eficacia_acao = nr_seq_motivo_p
	where	nr_sequencia		= nr_sequencia_p;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
