CREATE OR REPLACE PROCEDURE qua_doc_trein_pf_aval_update ( nr_seq_aval_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


/*
ie_opcao_p = L	libera avaliação
ie_opcao_p = E	estorna da avaliação
*/
BEGIN
if (ie_opcao_p = 'L') then
	update	qua_doc_trein_pf_aval
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario_lib	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_aval_p;
	commit;

elsif (ie_opcao_p = 'E') then
	update	qua_doc_trein_pf_aval
	set	dt_liberacao	 = NULL,
		nm_usuario_lib	 = NULL,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p
	where	nr_sequencia	= nr_seq_aval_p;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
