CREATE OR REPLACE PROCEDURE qua_gerar_aprov_doc ( nr_seq_doc_p bigint, cd_pessoa_aprov_p text, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w	integer;


BEGIN

if (coalesce(nr_seq_doc_p,0) > 0) and (cd_pessoa_aprov_p IS NOT NULL AND cd_pessoa_aprov_p::text <> '') then
	select	count(*)
	into STRICT	qt_existe_w
	from	qua_doc_aprov
	where	nr_seq_doc	= nr_seq_doc_p
	and	cd_pessoa_aprov	= cd_pessoa_aprov_p;

	if (qt_existe_w = 0) then
		insert into qua_doc_aprov(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_doc,
			cd_pessoa_aprov,
			dt_aprovacao,
			CD_SETOR_ATENDIMENTO )
		values (	nextval('qua_doc_aprov_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_doc_p,
			cd_pessoa_aprov_p,
			null,
			null);
		commit;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
