CREATE OR REPLACE PROCEDURE qua_gerar_criterios_problema ( nr_seq_problema_p bigint, nm_usuario_p text) AS $body$
DECLARE



ie_tipo_analise_w	varchar(15);
nr_seq_criterio_w	bigint;
cd_estabelecimento_w	smallint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	qua_crit_problema
where	ie_tipo_analise = ie_tipo_analise_w
and	cd_estabelecimento = cd_estabelecimento_w;


BEGIN

cd_estabelecimento_w	:=	wheb_usuario_pck.get_cd_estabelecimento;

select	ie_tipo_analise
into STRICT	ie_tipo_analise_w
from	qua_analise_problema
where	nr_sequencia = nr_seq_problema_p;

open C01;
loop
fetch C01 into
	nr_seq_criterio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into qua_analise_problema_crit(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_analise,
				nr_seq_crit_valor,
				qt_valor)
			values (	nextval('qua_analise_problema_crit_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_problema_p,
				nr_seq_criterio_w,
				0);
	end;
end loop;
close C01;

commit;

end;
