CREATE OR REPLACE PROCEDURE qua_gerar_doc_rev_aprovacao ( nr_seq_revisao_p bigint, dt_aprovacao_p timestamp, cd_pessoa_aprov_p text, nm_usuario_p text) AS $body$
BEGIN

if (nr_seq_revisao_p IS NOT NULL AND nr_seq_revisao_p::text <> '') then
	begin
	
	update	qua_doc_revisao_aprovacao
	set	dt_atualizacao = clock_timestamp(),
