		dt_aprovacao = dt_aprovacao_p
	where	nr_seq_revisao = nr_seq_revisao_p
	and 	cd_pessoa_aprov = cd_pessoa_aprov_p
	and 	coalesce(dt_aprovacao::text, '') = '';
	
