CREATE OR REPLACE PROCEDURE qua_gerar_itens_auditar ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w			bigint;
nr_seq_evento_w			bigint;
nr_seq_tipo_analise_w		bigint;
nr_seq_questao_w		bigint;
nr_seq_apres_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_seq_apres
	from	qua_questao_analise a,
		qua_grupo_analise b
	where	a.nr_seq_grupo = b.nr_sequencia
	and	b.nr_seq_tipo = nr_seq_tipo_analise_w
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	coalesce(b.ie_situacao,'A') = 'A';


BEGIN
select	b.nr_seq_tipo_analise
into STRICT	nr_seq_tipo_analise_w
from	qua_evento b,
	qua_evento_paciente a
where	a.nr_sequencia  = nr_sequencia_p
and	a.nr_seq_evento = b.nr_sequencia;

OPEN C01;
LOOP
FETCH C01 INTO
	nr_seq_questao_w,
	nr_seq_apres_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('qua_evento_pac_questao_seq')
	into STRICT	nr_sequencia_w
	;

	insert	into qua_evento_pac_questao(
		nr_sequencia,
		nr_seq_evento,
		nr_seq_questao,
		dt_atualizacao,
		nm_usuario,
		nr_seq_apres)
	values (
		nr_sequencia_w,
		nr_sequencia_p,
		nr_seq_questao_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w);
	end;
END LOOP;
CLOSE C01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
