		values (	nextval('qua_doc_log_acesso_seq'),
			nr_seq_docto_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_partic_w,
			clock_timestamp(),
			clock_timestamp(),
			'C',
			wheb_usuario_pck.get_cd_estabelecimento,
