SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qua_gerar_nao_conformidade ( nr_sequencia_p bigint, nr_seq_tipo_p bigint, cd_estabelecimento_p bigint, cd_pf_abertura_p text, dt_nao_conformidade_p timestamp default clock_timestamp(), ie_trigger_p text DEFAULT NULL, nm_usuario_p text DEFAULT NULL, ie_dano_paciente_p text default 'N') AS $body$
DECLARE

				

cd_setor_atendimento_w		bigint;
ds_evento_w			varchar(4000);
ds_historico_w			varchar(4000);
nr_sequencia_w			bigint;
nm_pessoa_abertura_w		varchar(255);
dt_abertura_w			timestamp;
cd_fornecedor_w			varchar(14);
nr_seq_indicador_w		bigint := null;
cd_setor_resp_w			varchar(15);
cd_setor_eficacia_w		bigint := null;
cd_pf_eficacia_w		varchar(15) := null;
cd_pf_resp_ocorrencia_w		varchar(10) := null;
qr_dia_analise_eficacia_w	bigint := null;
cd_perfil_ativo_w		bigint;
ie_carrega_pf_eficacia_w	varchar(2);
ds_disposicao_imediata_w	varchar(4000);
qr_dia_analise_eficacia2_w	bigint;
qt_dias_espera_w		integer;
qt_dias_espera2_w		varchar(5);
dt_esperada_w			timestamp;
dt_prevista_w			timestamp;		
nr_atendimento_w		bigint;
dt_evento_w			timestamp;
ie_setor_nc_evento_w		varchar(1);		
cd_setor_resp_param_w		varchar(15);
cd_setor_usuario_w		usuario.cd_setor_atendimento%type := coalesce(wheb_usuario_pck.get_cd_setor_atendimento,obter_setor_usuario(nm_usuario_p));


BEGIN

dt_abertura_w := coalesce(dt_nao_conformidade_p, clock_timestamp());

cd_perfil_ativo_w:= Obter_perfil_Ativo;

ie_carrega_pf_eficacia_w := obter_param_usuario(4000, 111, cd_perfil_ativo_w, nm_usuario_p, cd_estabelecimento_p, ie_carrega_pf_eficacia_w);
begin
	qt_dias_espera_w :=  coalesce(obter_valor_param_usuario(4000 ,89, cd_perfil_ativo_w, nm_usuario_p, cd_estabelecimento_p),0);
exception
when others then
	qt_dias_espera_w := 0;
end;
qt_dias_espera2_w := obter_param_usuario(4000, 90, cd_perfil_ativo_w, nm_usuario_p, cd_estabelecimento_p, qt_dias_espera2_w);
ie_setor_nc_evento_w := obter_param_usuario(4000, 334, cd_perfil_ativo_w, nm_usuario_p, cd_estabelecimento_p, ie_setor_nc_evento_w);

begin
qt_dias_espera2_w :=  coalesce(obter_valor_param_usuario(4000 ,90, cd_perfil_ativo_w, nm_usuario_p, cd_estabelecimento_p),0);
exception
when others then
	qt_dias_espera2_w := 0;
end;

select	cd_setor_atendimento,
	ds_evento,
	dt_evento,
	ds_disposicao_imediata,
	nr_atendimento,
	cd_setor_analise

into STRICT	cd_setor_atendimento_w,
	ds_evento_w,
	dt_evento_w,
	ds_disposicao_imediata_w,
	nr_atendimento_w,
	cd_setor_resp_param_w

from	qua_evento_paciente
where	nr_sequencia = nr_sequencia_p;

if (ds_disposicao_imediata_w = '' or coalesce(ds_disposicao_imediata_w::text, '') = '') then
	SELECT	ds_disp_imediata_padrao
	INTO STRICT	ds_disposicao_imediata_w

	FROM	qua_tipo_nao_conform
	WHERE	nr_sequencia = nr_seq_tipo_p;
end if;


select	nextval('qua_nao_conformidade_seq')
into STRICT	nr_sequencia_w
;

select	substr(max(vl_default),1,14)
into STRICT	cd_fornecedor_w
FROM	tabela_atrib_regra
WHERE 	nm_tabela = 'QUA_NAO_CONFORMIDADE'
AND 	nm_atributo = 'CD_FORNECEDOR'
AND	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
AND	coalesce(cd_perfil,cd_perfil_ativo_w)	= cd_perfil_ativo_w
AND 	coalesce(nm_usuario_param, nm_usuario_p) 	= nm_usuario_p;

select	substr(max(vl_default),1,10)
into STRICT	nr_seq_indicador_w
FROM	tabela_atrib_regra
WHERE 	nm_tabela = 'QUA_NAO_CONFORMIDADE'
AND 	nm_atributo = 'NR_SEQ_INDICADOR'
AND	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
AND	coalesce(cd_perfil,cd_perfil_ativo_w)	= cd_perfil_ativo_w
AND 	coalesce(nm_usuario_param, nm_usuario_p) 	= nm_usuario_p;

if (coalesce(ie_setor_nc_evento_w,'N') = 'N') then

	begin
	select	substr(replace(upper(max(vl_default)), '@SETOR_USUARIO', cd_setor_usuario_w),1,15)
	into STRICT	cd_setor_resp_w
	FROM	tabela_atrib_regra
	WHERE 	nm_tabela = 'QUA_NAO_CONFORMIDADE'
	AND 	nm_atributo = 'CD_SETOR_RESP'
	AND	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
	AND	coalesce(cd_perfil,cd_perfil_ativo_w)	= cd_perfil_ativo_w
	AND 	coalesce(nm_usuario_param, nm_usuario_p) 	= nm_usuario_p;

	select	cd_setor_atendimento
	into STRICT	cd_setor_resp_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_resp_w;
	exception
	when others then
	cd_setor_resp_w := null;
	end;
	
	if (coalesce(cd_setor_resp_w::text, '') = '' or cd_setor_resp_w = '0') then
		SELECT	cd_setor_resp_padrao
		INTO STRICT	cd_setor_resp_w
		FROM	qua_tipo_nao_conform
		WHERE	nr_sequencia = nr_seq_tipo_p;
	end if;

else
	cd_setor_resp_w	:= 	cd_setor_resp_param_w;

end if;

begin
select	substr(replace(upper(max(vl_default)), '@SETOR_USUARIO', cd_setor_usuario_w),1,15)
into STRICT	cd_setor_eficacia_w
FROM	tabela_atrib_regra
WHERE 	nm_tabela = 'QUA_NAO_CONFORMIDADE'
AND 	nm_atributo = 'CD_SETOR_EFICACIA'
AND	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
AND	coalesce(cd_perfil,cd_perfil_ativo_w)	= cd_perfil_ativo_w
AND 	coalesce(nm_usuario_param, nm_usuario_p) 	= nm_usuario_p;

select	cd_setor_atendimento
into STRICT	cd_setor_eficacia_w
from	setor_atendimento
where	cd_setor_atendimento = cd_setor_eficacia_w;
exception
when others then
	cd_setor_eficacia_w := null;
end;

begin
select	substr(replace(upper(max(vl_default)), '@PESSOA_FISICA', obter_cod_pf_usuario(nm_usuario_p)),1,15)

into STRICT	cd_pf_eficacia_w
FROM	tabela_atrib_regra
WHERE 	nm_tabela = 'QUA_NAO_CONFORMIDADE'
AND 	nm_atributo = 'CD_PF_RESP_EFICACIA'
AND	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
AND	coalesce(cd_perfil,cd_perfil_ativo_w)	= cd_perfil_ativo_w
AND 	coalesce(nm_usuario_param, nm_usuario_p) 	= nm_usuario_p;

select	cd_pessoa_fisica
into STRICT	cd_pf_eficacia_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pf_eficacia_w;
exception
when others then
cd_pf_eficacia_w := null;
end;

if (coalesce(cd_pf_eficacia_w::text, '') = '' or cd_pf_eficacia_w = '') then
	
	SELECT	cd_pf_resp_eficacia_padrao
	INTO STRICT	cd_pf_eficacia_w
	FROM	qua_tipo_nao_conform
	WHERE	nr_sequencia = nr_seq_tipo_p;
	
end if;

begin
select	substr(replace(upper(max(vl_default)), '@PESSOA_FISICA', obter_cod_pf_usuario(nm_usuario_p)),1,10)

into STRICT	cd_pf_resp_ocorrencia_w
FROM	tabela_atrib_regra
WHERE 	nm_tabela = 'QUA_NAO_CONFORMIDADE'
AND 	nm_atributo = 'CD_PF_RESP_OCORRENCIA'
AND	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
AND	coalesce(cd_perfil,cd_perfil_ativo_w)	= cd_perfil_ativo_w
AND 	coalesce(nm_usuario_param, nm_usuario_p) 	= nm_usuario_p;

select	cd_pessoa_fisica
into STRICT	cd_pf_resp_ocorrencia_w
from	pessoa_fisica
where	cd_pessoa_fisica = cd_pf_resp_ocorrencia_w;
exception
when others then
cd_pf_resp_ocorrencia_w := null;
end;

select	substr(max(vl_default),1,15)
into STRICT	qr_dia_analise_eficacia_w
FROM	tabela_atrib_regra
WHERE 	nm_tabela = 'QUA_NAO_CONFORMIDADE'
AND 	nm_atributo = 'QR_DIA_ANALISE_EFICACIA'
AND	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
AND	coalesce(cd_perfil,cd_perfil_ativo_w)	= cd_perfil_ativo_w
AND 	coalesce(nm_usuario_param, nm_usuario_p) 	= nm_usuario_p;

if ( coalesce(qr_dia_analise_eficacia_w::text, '') = '' or qr_dia_analise_eficacia_w = 0 ) then
	SELECT	qt_dia_espera
	INTO STRICT	qr_dia_analise_eficacia_w
	FROM	qua_tipo_nao_conform
	WHERE	nr_sequencia = nr_seq_tipo_p;
end if;

SELECT	qt_dia_espera
INTO STRICT	qr_dia_analise_eficacia2_w
FROM	qua_tipo_nao_conform
WHERE	nr_sequencia = nr_seq_tipo_p;

dt_esperada_w := coalesce(dt_abertura_w, clock_timestamp()) + coalesce(qr_dia_analise_eficacia2_w, coalesce(qt_dias_espera_w, 0));
dt_prevista_w := qua_obter_data_util_periodo(cd_estabelecimento_p,coalesce(dt_abertura_w, clock_timestamp()), coalesce(qt_dias_espera2_w, 0));

if (coalesce( ie_carrega_pf_eficacia_w,'N') = 'S') then
	select	max(cd_pessoa_resp)
	into STRICT	cd_pf_eficacia_w
	from	setor_atendimento
	where	cd_setor_atendimento = cd_setor_atendimento_w;
end if;

insert	into qua_nao_conformidade(
		nr_sequencia,
		cd_estabelecimento,
		dt_evento,
		dt_atualizacao,
		nm_usuario,
		dt_abertura,
		cd_pf_abertura,
		ds_nao_conformidade,
		nr_seq_indicador,
		cd_setor_atendimento,
		cd_setor_resp,
		cd_setor_eficacia,
		nm_usuario_origem,
		ie_status,
		nr_seq_tipo,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pf_resp_eficacia,
		cd_fornecedor,
		cd_pf_resp_ocorrencia,
		qr_dia_analise_eficacia,
		nr_seq_evento,
		ie_dano_paciente,
		ds_disposicao_imediata,
		dt_esperada,
		dt_prevista,
		nr_atendimento)
values (		nr_sequencia_w,
		cd_estabelecimento_p,
		dt_evento_w,
		dt_abertura_w,
		nm_usuario_p,
		dt_abertura_w,
		cd_pf_abertura_p,
		ds_evento_w,
		nr_seq_indicador_w,
		cd_setor_atendimento_w,
		cd_setor_resp_w,
		cd_setor_eficacia_w,
		nm_usuario_p,
		(SELECT vl_dominio from VALOR_DOMINIO where    cd_dominio = 1397 and vl_dominio='Aberta'),
		nr_seq_tipo_p,
		dt_abertura_w,
		nm_usuario_p,
		cd_pf_eficacia_w,
		cd_fornecedor_w,
		cd_pf_resp_ocorrencia_w,
		qr_dia_analise_eficacia_w,
		nr_sequencia_p,
		ie_dano_paciente_p,
		ds_disposicao_imediata_w,
		dt_esperada_w,
		dt_prevista_w,
		nr_atendimento_w);
		
select	substr(obter_nome_pf(cd_pf_abertura_p),1,255)
into STRICT	nm_pessoa_abertura_w
;

ds_historico_w	:=	WHEB_MENSAGEM_PCK.get_texto(316413, 'NR_SEQUENCIA='|| nr_sequencia_w ||
									';NM_PESSOA_ABERTURA_W='|| nm_pessoa_abertura_w || 
									';DT_ABERTURA_W=' ||to_char(dt_abertura_w,'dd/mm/yyyy hh24:mm:ss'));

insert into qua_evento_hist(
		nr_sequencia,
		nr_seq_evento,
		dt_atualizacao,
		nm_usuario,
		ds_historico,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
values (	nextval('qua_evento_hist_seq'),
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_historico_w,
		clock_timestamp(),
		nm_usuario_p);

if (coalesce(ie_trigger_p,'N') = 'N') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE qua_gerar_nao_conformidade ( nr_sequencia_p bigint, nr_seq_tipo_p bigint, cd_estabelecimento_p bigint, cd_pf_abertura_p text, dt_nao_conformidade_p timestamp default clock_timestamp(), ie_trigger_p text DEFAULT NULL, nm_usuario_p text DEFAULT NULL, ie_dano_paciente_p text default 'N') FROM PUBLIC;

