CREATE OR REPLACE PROCEDURE qua_gerar_nao_conform_probl ( nr_seq_problema_p bigint, nr_seq_tipo_p bigint, nm_usuario_p text, cd_setor_atendimento_p bigint, nr_seq_nc_p INOUT bigint) AS $body$
DECLARE

				 
cd_estabelecimento_w	smallint;
cd_pessoa_resp_w		varchar(10);
ds_problema_w		varchar(4000);
nr_sequencia_w		bigint;
cd_pessoa_abertura_w	varchar(10);
dt_abertura_w		timestamp;


BEGIN 
 
select	cd_estabelecimento, 
	cd_pessoa_resp, 
	ds_problema, 
	substr(obter_pessoa_fisica_usuario(nm_usuario_p,'C'),1,10), 
	clock_timestamp() 
into STRICT	cd_estabelecimento_w, 
	cd_pessoa_resp_w, 
	ds_problema_w, 
	cd_pessoa_abertura_w, 
	dt_abertura_w 
from	qua_analise_problema 
where	nr_sequencia = nr_seq_problema_p;
 
select	nextval('qua_nao_conformidade_seq') 
into STRICT	nr_sequencia_w
;
 
insert	into qua_nao_conformidade( 
		nr_sequencia, 
		cd_estabelecimento, 
		dt_atualizacao, 
		nm_usuario, 
		dt_abertura, 
		cd_pf_abertura, 
		ds_nao_conformidade, 
		ds_disposicao_imediata, 
		nr_seq_indicador, 
		cd_setor_atendimento, 
		cd_setor_resp, 
		cd_setor_eficacia, 
		dt_esperada, 
		dt_prevista, 
		dt_encerramento, 
		nm_usuario_origem, 
		ie_status, 
		nr_seq_tipo, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		cd_pf_resp_eficacia, 
		cd_fornecedor, 
		cd_pf_resp_ocorrencia, 
		nr_seq_motivo_cancel, 
		qr_dia_analise_eficacia) 
values (		nr_sequencia_w, 
		cd_estabelecimento_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		dt_abertura_w, 
		cd_pessoa_abertura_w, 
		ds_problema_w, 
		'', 
		null, 
		cd_setor_atendimento_p, 
		null, 
		null, 
		'', 
		'', 
		'', 
		nm_usuario_p, 
		'Aberta', 
		nr_seq_tipo_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		null, 
		null, 
		cd_pessoa_resp_w, 
		null, 
		null);
 
update	qua_analise_problema 
set	nr_seq_rnc	= nr_sequencia_w, 
	dt_geracao_rnc	= dt_abertura_w 
where	nr_sequencia	= nr_seq_problema_p;
 
commit;
 
nr_seq_nc_p	:= nr_sequencia_w;
 
end;
$body$
