SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE qua_gerar_os_rev_doc_aut ( nm_usuario_p text) AS $body$
DECLARE


nr_seq_doc_w		bigint;
nr_seq_os_w		bigint;
--ds_dano_w		varchar2(255) := 'Este documento esta a demasiado tempo sem revisao e e necessaria avaliacao, identificando pontos de melhoria e adequacao do processo, de acordo com a conformidade do mesmo';
ds_dano_w		varchar(255) := substr(wheb_mensagem_pck.get_texto(305957),1,255);
ie_base_corp_w 		varchar(1) := 'N';
ie_base_wheb_w 		varchar(1) := 'N';
ie_base_philips_w		varchar(1) := 'N';

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	qua_documento a
	where	a.ie_situacao = 'A'
	and	a.qt_dias_revisao > 0
	and	a.ie_status not in ('R','V')
	and	trunc(coalesce(to_date(substr(qua_obter_dados_documento(a.nr_sequencia,'UAR'),1,19),'dd/mm/yyyy hh24:mi:ss'), coalesce(a.dt_revalidacao,
			a.dt_aprovacao)),'dd') + coalesce(qt_dias_revisao,0) < clock_timestamp()
	and not exists (SELECT	1
			from	man_ordem_servico b
			where	b.ie_status_ordem <> 3
			and	b.nr_seq_documento	= a.nr_sequencia
			and	b.ie_tipo_ordem		= 12)
	and	((coalesce(nr_seq_superior::text, '') = '' and ie_base_philips_w = 'S') or (ie_base_philips_w = 'N'));


BEGIN
ie_base_corp_w := obter_se_base_corp;
ie_base_wheb_w := obter_se_base_wheb;

if (ie_base_corp_w = 'S') or (ie_base_wheb_w = 'S') then
	ie_base_philips_w := 'S';
end if;

open C01;
loop
fetch C01 into	
	nr_seq_doc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qua_gerar_os_revisao_doc(nr_seq_doc_w, ds_dano_w, nm_usuario_p, nr_seq_os_w,'');
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
