CREATE OR REPLACE PROCEDURE qua_gerar_partic_ata_setor ( nr_sequencia_p bigint, cd_setor_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);

C01 CURSOR FOR
	SELECT	a.cd_pessoa_fisica
	from	usuario a
	where	a.ie_situacao = 'A'
	and	(a.cd_pessoa_fisica IS NOT NULL AND a.cd_pessoa_fisica::text <> '')
	and	a.cd_setor_atendimento = cd_setor_p
	and not exists (	SELECT	1
			from	proj_ata_participante x
			where	x.cd_pessoa_participante = a.cd_pessoa_fisica
			and	x.nr_seq_ata = nr_sequencia_p)
	group by a.cd_pessoa_fisica;


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) and (coalesce(cd_setor_p,0) > 0) then
	begin
	open C01;
	loop
	fetch C01 into
		cd_pessoa_fisica_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		insert into proj_ata_participante(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_ata,
					ie_faltou,
					nm_pessoa_participante,
					ds_setor,
					cd_pessoa_participante,
					dt_ciente_ata,
					cd_setor_atendimento,
					ds_observacao,
					ds_email,
					nm_pessoa_adicional)
				values (	nextval('proj_ata_participante_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_sequencia_p,
					'N',
					null,
					null,
					cd_pessoa_fisica_w,
					null,
					cd_setor_p,
					null,
					null,
					null);
		end;
	end loop;
	close C01;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
